const imageBase64 = $input.first().json.body.imageBase64;

const requestBody = {
  model: "anthropic/claude-sonnet-4",
  temperature: 0,
  messages: [
    {
      role: "user",
      content: [
        {
          type: "text",
          text: "Aflæs opskriften fra billedet. Returner PRÆCIS JSON i dette format uden markdown-blokke:\n{\n  \"titel\": \"..\",\n  \"portioner\": <tal>,\n  \"ingredienser\": [\"ingrediens 1\", \"ingrediens 2\", ...],\n  \"fremgangsmaade\": \"...\"\n}\n\nKRITISK VIGTIGT FOR INGREDIENSER:\n1. Tæl HVER ENESTE ingrediens på billedet - medtag dem ALLE\n2. Inkluder mængder og enheder (fx \"400g hakket oksekød\", \"2 spsk olie\")\n3. Ingredienser kan stå i kolonner eller lister - læs dem ALLE\n4. Dobbelttjek at du har medtaget samtlige ingredienser før du svarer\n5. Hvis der er 15 ingredienser på billedet, skal arrayet have 15 elementer"
        },
        { type: "image_url", image_url: { url: imageBase64 } }
      ]
    }
  ]
};
return [{ json: requestBody }];

try {
  const content = $input.first().json.choices?.[0]?.message?.content;
  if (!content) {
    return [{ json: { success: false, error: 'Ingen respons fra AI' } }];
  }

  // Check if AI couldn't read image
  const lower = content.toLowerCase();
  if (lower.includes("i'm sorry") || lower.includes("i cannot") || lower.includes("jeg kan ikke")) {
    return [{ json: { success: false, error: 'AI kunne ikke læse opskriften fra billedet.' } }];
  }

  // Try to extract JSON from various formats
  let jsonStr = content.trim();

  // Remove markdown code blocks
  if (jsonStr.includes('```json')) {
    jsonStr = jsonStr.split('```json')[1].split('```')[0];
  } else if (jsonStr.includes('```')) {
    jsonStr = jsonStr.split('```')[1].split('```')[0];
  }

  // Find JSON object in text (starts with { ends with })
  const jsonMatch = jsonStr.match(/{[sS]*}/);
  if (jsonMatch) {
    jsonStr = jsonMatch[0];
  }

  const data = JSON.parse(jsonStr.trim());
  return [{ json: { success: true, data } }];
} catch (e) {
  // Return the raw content for debugging
  const raw = $input.first().json.choices?.[0]?.message?.content || 'No content';
  return [{ json: { success: false, error: 'Parse fejl: ' + e.message, raw: raw.substring(0, 500) } }];
}
